import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFlashlight, faEyeSlash } from '@fortawesome/free-solid-svg-icons';
import './PasswordReveal.css';

const PasswordReveal = () => {
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  return (
    <div className="password-container">
      <input
        type={showPassword ? 'text' : 'password'}
        value={password}
        onChange={handlePasswordChange}
        placeholder="Enter your password"
      />
      <button onClick={togglePasswordVisibility} className="toggle-button">
        <FontAwesomeIcon icon={showPassword ? faEyeSlash : faFlashlight} />
      </button>
    </div>
  );
};

export default PasswordReveal;
