import React, { useState } from 'react';
import '../style/PassWordReveal.css';
import FlashlightOn from '../assets/icons/Flash_on.png';
import FlashlightOff from '../assets/icons/Flash_off.png';

const PassWordReveal = () => {
    const [passwordShown, setPasswordShown] = useState(false);

    const togglePasswordVisibility = () => {
        setPasswordShown(!passwordShown);
    };

    const passwordInput = document.getElementById('password');
    const toggleIcon = document.getElementById('toggleIcon');
    const container = document.querySelector('.light-container');

    if (passwordInput.type === "password") {
        passwordInput.type = "text";
        toggleIcon.src = "Flash_on.png"; // Change to the 'light on' icon
        container.classList.add('light-on'); // Add the light-on class
    } else {
        passwordInput.type = "password";
        toggleIcon.src = "Flash_off.png"; // Change to the 'light off' icon
        container.classList.remove('light-on'); // Remove the light-on class
    }

  return (
        <div className="password-container">
          <div className='h1_css'>
          <h1>PassWord Reveal</h1>
          </div>

          <div className='input_container'>
            <input 
                  type={passwordShown ? "text" : "password"} 
                  className="password-input" 
                  placeholder="Enter your password" 
              />
              <img 
                  src={passwordShown ? FlashlightOn : FlashlightOff} 
                  onClick={togglePasswordVisibility} 
                  alt="Toggle password visibility" 
                  className="password-toggle-icon" 
              />
          </div>
            
        </div>
    );
  };

export default PassWordReveal;
